mapper.select:
SELECT 

FROM test_data11 a

WHERE a.id = #{id}

SELECT
FROM test_data11 a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM test_data11 a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE test_data11 SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
